import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Dashboard = () => {

  //const [isLinked, setIsLinked] = useState(false);
 // const [analyticsData, setAnalyticsData] = useState(null);

 const [profileData, setProfileData] = useState(null); //inst basic
  const [mediaData, setMediaData] = useState([]);

  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

useEffect(() => {//instagram basic
    const fetchBasicData = async () => {
      try {
        const response = await axios.get('/api/instagram/basic-data');
        setProfileData(response.data.profile);
        setMediaData(response.data.media);
      } catch (err) {
        console.error('Error fetching basic Instagram data:', err);
        setError('Failed to fetch basic Instagram data.');
      } finally {
        setLoading(false);
      }
    };

    fetchBasicData();
  }, []);
  /*useEffect(() => {
    checkInstagramLink();
  }, []);

  const checkInstagramLink = async () => {
    try {
      const response = await axios.get('/api/instagram/check-link');
      setIsLinked(response.data.isLinked);
      if (response.data.isLinked) {
        fetchAnalyticsData();
      }
    } catch (error) {
      console.error('Error checking Instagram link:', error);
      setError('Error checking Instagram link.');
    } finally {
      setLoading(false);
    }
  };

  const fetchAnalyticsData = async () => {
    try {
      const response = await axios.get('/api/instagram/analytics');
      setAnalyticsData(response.data);
    } catch (error) {
      console.error('Error fetching analytics data:', error);
      setError('Failed to fetch analytics data.');
    }
  };

  const handleLinkInstagram = () => {
    window.location.href = '/api/instagram/oauth';
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  if (!isLinked) {
    return (
      <div className="dashboard">
        <h1>Instagram Analytics Dashboard</h1>
        <button onClick={handleLinkInstagram}>Link Instagram Account</button>
      </div>
    );
  }
*/
return (
  <div className="dashboard">
    <h1>Instagram Basic Data Dashboard</h1>
    {profileData && (
      <div>
        <h2>Profile Information</h2>
        <p>Username: {profileData.username}</p>
        {/* Display more profile data as needed */}
      </div>
    )}

    {mediaData.length > 0 && (
      <div>
        <h2>Media Gallery</h2>
        {mediaData.map((media, index) => (
          <div key={index}>
            <p>{media.caption}</p>
            <img src={media.media_url} alt={media.caption} />
            {/* Display more media details as needed */}
          </div>
        ))}
      </div>
    )}
  </div>
);
};

export default Dashboard;
