import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Dashboard = () => {

const [isLinked, setIsLinked] = useState(false);
const [mediaData, setMediaData] = useState([]);
const [profileData, setProfileData] = useState(null);

const [loading, setLoading] = useState(true);
const [error, setError] = useState('');
const navigate = useNavigate()
const [plannedPosts, setPlannedPosts] = useState([]);

const [isTwitterLinked, setIsTwitterLinked] = useState(false);
const [twitterData, setTwitterData] = useState(null); // Stores tweets or other Twitter data
const [twitterProfile, setTwitterProfile] = useState(null); 

  
  useEffect(() => {
    checkInstagramLink();
    checkTwitterLink();
    fetchBasicData();
    fetchPlannedPosts();
    fetchTwitterData();
    fetchTwitterProfile();
  }, []);

  const checkTwitterLink = async () => {
    try {
      // Replace with your API endpoint to check Twitter link status
      const response = await axios.get('/api/twitter/check-twitter-link');
      setIsTwitterLinked(response.data.isLinked);
    } catch (error) {
      console.error('Error checking Twitter link:', error);
      // Handle error
    }
  };

  const fetchTwitterProfile = async () => {
    try {
      const response = await axios.get('/api/twitter/my-profile');
      setTwitterProfile(response.data.profile); // Update state with Twitter profile data
    } catch (error) {
      console.error('Error fetching Twitter profile:', error);
    }
  };

  const fetchTwitterData = async () => {
    try {
      const response = await axios.get('/api/twitter/fetch-tweets');
      setTwitterData(response.data);
    } catch (error) {
      console.error('Error fetching Twitter data:', error);
      // Handle error
    }
  };

  const handleLinkTwitter = () => {
    // Redirect to the route that initiates Twitter OAuth
    window.location.href = '/api/twitter/link-twitter';
  };

  const fetchPlannedPosts = async () => {
    try {
      const response = await axios.get('/api/auth/posts'); 
      setPlannedPosts(response.data);
    } catch (error) {
      console.error('Error fetching planned posts:', error);
      
    }
  };

  const checkInstagramLink = async () => {
    try {
      const response = await axios.get('/api/instagram/check-link');
      setIsLinked(response.data.isLinked);
      if (response.data.isLinked) {
        fetchBasicData();
      }
    } catch (error) {
      console.error('Error checking Instagram link:', error);
      setError('Error checking Instagram link.');
    } finally {
      setLoading(false);
    }
  };

  
  const fetchBasicData = async () => {
    try {
      const response = await axios.get('/api/instagram/basic-data');
      setProfileData(response.data.profile);
      setMediaData(response.data.media);
    } catch (error) {
      console.error('Error fetching basic Instagram data:', error);
    }
  };

  const handleLinkInstagram = () => {
    window.location.href = '/api/instagram/oauth';
  };
  
  const navigateToScheduler = () => {
    navigate('/post-scheduler'); 
  };

  const sampleChartData = [
    { name: 'Jan', uv: 400, pv: 2400, amt: 2400 },
    { name: 'Feb', uv: 300, pv: 1398, amt: 2210 },
    // ... more data
  ];

  if (loading) { 
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  if (!isLinked) {
    return (
      <div className="dashboard">
        <h1>Instagram Analytics Dashboard</h1>
        <button onClick={handleLinkInstagram}>Link Instagram Account</button>
      </div>
    );
  }

  return (
    <div className="dashboard">
      <header className="dashboard-header">
        <h1>Social Media Analytics Dashboard</h1>
        <nav>
          <button onClick={navigateToScheduler}>Go to Post Scheduler</button>
          {isLinked ? (
            <button onClick={handleLinkInstagram}>Re-link Instagram</button>
          ) : (
            <button onClick={handleLinkInstagram}>Link Instagram</button>
          )}
          {isTwitterLinked ? (
            <button onClick={handleLinkTwitter}>Re-link Twitter</button>
          ) : (
            <button onClick={handleLinkTwitter}>Link Twitter</button>
          )}
        </nav>
      </header>

      {loading ? (
        <div className="loading">Loading...</div>
      ) : error ? (
        <div className="error">Error: {error}</div>
      ) : (
        <>
          <div className="account-info">
            {profileData && (
              <div className="instagram-info">
                <h2>Instagram Profile Information</h2>
                <p>Username: {profileData.username}</p>
                <p>Account Type: {profileData.account_type}</p>
                <p>Media Count: {profileData.media_count}</p>
              </div>
            )}

            {twitterProfile && (
              <div className="twitter-info">
                <h2>Twitter Profile Information</h2>
                <p>Username: {twitterProfile.username}</p>
                <p>Followers: {twitterProfile.followers_count}</p>
                {/* Add more Twitter profile details as needed */}
              </div>
            )}
          </div>

          <div className="media-sections">
            {mediaData.length > 0 && (
              <div className="instagram-media">
                <h2>Recent Instagram Media:</h2>
                <div className="media-grid">
                  {mediaData.map((media, index) => (
                    <div key={index} className="media-item instagram-item">
                      <img src={media.media_url} alt={media.caption} />
                      <p>{media.caption}</p>
                    </div>
                  ))}
                </div>
              </div>
            )}

            {twitterData && (
              <div className="twitter-media">
                <h2>Latest Tweets:</h2>
                <div className="twitter-feed">
                  {twitterData.map((tweet, index) => (
                    <div key={index} className="tweet-item">
                      <p>{tweet.text}</p>
                      {/* Additional tweet details as needed */}
                    </div>
                  ))}
                </div>
              </div>
            )}
          </div>

          <div className="section">
            <h2>Planned Posts</h2>
            {plannedPosts.length > 0 ? (
              <div className="media-grid">
                {plannedPosts.map((post, index) => (
                  <div key={index} className="media-item">
                    <h3>{post.title}</h3>
                    <p>{post.description}</p>
                    {post.image && (
                      <img src={post.image} alt={post.title} />
                    )}
                    <p className="planned-date">
                      Planned for: {new Date(post.scheduledTime).toLocaleDateString()}
                    </p>
                  </div>
                ))}
              </div>
            ) : (
              <p>No planned posts available.</p>
            )}
          </div>
        </>
      )}
    </div>
  );
};

export default Dashboard;
