import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { LineChart, Line, CartesianGrid, XAxis, YAxis, Tooltip, ResponsiveContainer } from 'recharts';

const Dashboard = () => {
    const [isInstagramLinked, setIsInstagramLinked] = useState(false);
    const [isTwitterLinked, setIsTwitterLinked] = useState(false);
    const [instagramData, setInstagramData] = useState(null);
    const [twitterData, setTwitterData] = useState(null);
    const [plannedPosts, setPlannedPosts] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        checkInstagramLink();
        checkTwitterLink();
        fetchInstagramData();
        fetchPlannedPosts();
        fetchTwitterData();
    }, []);

    const checkInstagramLink = async () => {
        try {
            const response = await axios.get('/api/instagram/check-link');
            setIsInstagramLinked(response.data.isLinked);
        } catch (error) {
            console.error('Error checking Instagram link:', error);
        }
    };

    const checkTwitterLink = async () => {
        try {
            const response = await axios.get('/api/twitter/check-link');
            setIsTwitterLinked(response.data.isLinked);
        } catch (error) {
            console.error('Error checking Twitter link:', error);
        }
    };

    const fetchInstagramData = async () => {
        if (!isInstagramLinked) return;
        try {
            const response = await axios.get('/api/instagram/data');
            setInstagramData(response.data);
        } catch (error) {
            console.error('Error fetching Instagram data:', error);
        }
    };

    const fetchPlannedPosts = async () => {
        try {
            const response = await axios.get('/api/posts');
            setPlannedPosts(response.data);
        } catch (error) {
            console.error('Error fetching planned posts:', error);
        }
    };

    const fetchTwitterData = async () => {
        if (!isTwitterLinked) return;
        try {
            const response = await axios.get('/api/twitter/data');
            setTwitterData(response.data);
        } catch (error) {
            console.error('Error fetching Twitter data:', error);
        }
    };

    const handleLinkInstagram = () => {
        window.location.href = '/api/instagram/oauth';
    };

    const handleLinkTwitter = () => {
        window.location.href = '/api/twitter/oauth';
    };

    const navigateToScheduler = () => {
        navigate('/post-scheduler');
    };

    const sampleChartData = [
        { name: 'Jan', uv: 400, pv: 2400, amt: 2400 },
        { name: 'Feb', uv: 300, pv: 1398, amt: 2210 },
    ];

    return (
        <div className="dashboard">
            <header className="dashboard-header">
                <h1>Social Media Analytics Dashboard</h1>
                <nav>
                    <button onClick={navigateToScheduler}>Go to Post Scheduler</button>
                    {isInstagramLinked ? <button onClick={handleLinkInstagram}>Re-link Instagram</button> : <button onClick={handleLinkInstagram}>Link Instagram</button>}
                    {isTwitterLinked ? <button onClick={handleLinkTwitter}>Re-link Twitter</button> : <button onClick={handleLinkTwitter}>Link Twitter</button>}
                </nav>
            </header>

            {loading ? (
                <div className="loading">Loading...</div>
            ) : error ? (
                <div className="error">Error: {error}</div>
            ) : (
                <>
                    <div className="account-info">
                        {instagramData && (
                            <div className="instagram-info">
                                <h2>Instagram Profile Information</h2>
                                {/* Display Instagram profile information */}
                            </div>
                        )}

                        {twitterData && (
                            <div className="twitter-info">
                                <h2>Twitter Profile Information</h2>
                                {/* Display Twitter profile information */}
                            </div>
                        )}
                    </div>

                    <div className="media-sections">
                        {instagramData && (
                            <div className="instagram-media">
                                <h2>Recent Instagram Media:</h2>
                                {/* Display Instagram media */}
                            </div>
                        )}

                        {twitterData && (
                            <div className="twitter-media">
                                <h2>Latest Tweets:</h2>
                                {/* Display tweets */}
                            </div>
                        )}
                    </div>

                    <div className="section">
                        <h2>Planned Posts</h2>
                        {plannedPosts.length > 0 ? (
                            <div className="media-grid">
                                {plannedPosts.map((post, index) => (
                                    <div key={index} className="media-item">
                                        {/* Display planned posts */}
                                    </div>
                                ))}
                            </div>
                        ) : (
                            <p>No planned posts available.</p>
                        )}
                    </div>
                </>
            )}
        </div>
    );
};

export default Dashboard;
