import React, { useState } from 'react';
import { Calendar, momentLocalizer } from 'react-big-calendar';
import moment from 'moment';
import Modal from 'react-modal';
import DatePicker from 'react-datepicker';
import TimePicker from 'react-time-picker';
import 'react-big-calendar/lib/css/react-big-calendar.css';
import 'react-datepicker/dist/react-datepicker.css';
import './PostScheduler.css';

const localizer = momentLocalizer(moment);

Modal.setAppElement('#root');

const PostScheduler = () => {
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [selectedTime, setSelectedTime] = useState('10:00');
  const [events, setEvents] = useState([]);
  const [postDetails, setPostDetails] = useState({ title: '', description: '' });

  const openModal = () => setModalIsOpen(true);
  const closeModal = () => setModalIsOpen(false);

  const handleDateChange = date => setSelectedDate(date);
  const handleTimeChange = time => setSelectedTime(time);
  const handleInputChange = e => setPostDetails({ ...postDetails, [e.target.name]: e.target.value });

  const handleSubmit = e => {
    e.preventDefault();
    const dateTime = new Date(moment(selectedDate).format('YYYY-MM-DD') + ' ' + selectedTime);
    const newEvent = {
      title: postDetails.title,
      start: dateTime,
      end: dateTime,
      allDay: false,
      resource: postDetails,
    };
    setEvents([...events, newEvent]);
    closeModal();
  };

  return (
    <div className="post-scheduler">
      <Calendar
        localizer={localizer}
        events={events}
        startAccessor="start"
        endAccessor="end"
        style={{ height: 500, margin: '50px' }}
        views={['month', 'week', 'day']}
      />
      <button className="add-post-btn" onClick={openModal}>Add Post</button>
      
      <Modal isOpen={modalIsOpen} onRequestClose={closeModal} className="modal">
        <form onSubmit={handleSubmit} className="schedule-form">
          <h2>Schedule a Post</h2>
          <input 
            type="text" 
            name="title" 
            value={postDetails.title}
            onChange={handleInputChange}
            placeholder="Title" 
            required
          />
          <textarea 
            name="description" 
            value={postDetails.description}
            onChange={handleInputChange}
            placeholder="Description"
            required
          />
          <div className="datetime-picker">
            <DatePicker selected={selectedDate} onChange={handleDateChange} />
            <TimePicker onChange={handleTimeChange} value={selectedTime} />
          </div>
          <div className="form-actions">
            <button type="submit" className="submit-btn">Schedule</button>
            <button onClick={closeModal} className="cancel-btn">Cancel</button>
          </div>
        </form>
      </Modal>
    </div>
  );
};

export default PostScheduler;
