import React, { useState, useEffect } from 'react';
import axios from 'axios';
 // Make sure to create a corresponding CSS file

const Dashboard = () => {
  const [analyticsData, setAnalyticsData] = useState({
    followerCount: 0,
    engagementRate: 0,
    totalLikes: 0,
    totalComments: 0,
    // Add more as needed
  });

  useEffect(() => {
    const fetchData = async () => {
        try {
            const response = await axios.get('/api/instagram/analytics');
            setAnalyticsData(response.data);
        } catch (error) {
            console.error('Error fetching analytics data:', error);
        }
    };

    fetchData();
}, []);

if (!analyticsData) {
    return <div>Loading analytics...</div>; // Loading state
}

  const handleLinkInstagram = () => {
    // Redirect user to Instagram OAuth
    window.location.href = '/api/instagram/oauth';
  };

  return (
    <div className="dashboard">
      <h1 className="title">Instagram Analytics Dashboard</h1>
      <button onClick={handleLinkInstagram} className="link-instagram-btn">
        Link Instagram Account
      </button>
      <div className="analytics-container">
        <div className="analytics-item">
          <h2>Follower Count</h2>
          <p>{analyticsData.followerCount}</p>
        </div>

        <div className="analytics-item">
          <h2>Engagement Rate</h2>
          <p>{analyticsData.engagementRate}%</p>
        </div>

        <div className="analytics-item">
          <h2>Total Likes</h2>
          <p>{analyticsData.totalLikes}</p>
        </div>

        <div className="analytics-item">
          <h2>Total Comments</h2>
          <p>{analyticsData.totalComments}</p>
        </div>

        {/* More analytics items can be added here */}
      </div>
    </div>
  );
};

export default Dashboard;
