import React, { useState } from 'react';
import { Calendar, momentLocalizer } from 'react-big-calendar';
import moment from 'moment';
import Modal from 'react-modal';
import DatePicker from 'react-datepicker';
import 'react-big-calendar/lib/css/react-big-calendar.css';
import './PostScheduler.css';
import 'react-datepicker/dist/react-datepicker.css';

const localizer = momentLocalizer(moment);

Modal.setAppElement('#root'); // Assuming your root element has an id of 'root'

const PostScheduler = () => {
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [events, setEvents] = useState([]);
  const [postDetails, setPostDetails] = useState({ title: '', description: '' });

  const openModal = () => setModalIsOpen(true);
  const closeModal = () => setModalIsOpen(false);

  const handleDateChange = date => setSelectedDate(date);
  const handleInputChange = e => setPostDetails({ ...postDetails, [e.target.name]: e.target.value });

  const handleSubmit = e => {
    e.preventDefault();
    // Adjust start and end times to selected date
    const start = new Date(moment(selectedDate).format('YYYY-MM-DD') + ' ' + moment(startTime).format('HH:mm:ss'));
    const end = new Date(moment(selectedDate).format('YYYY-MM-DD') + ' ' + moment(endTime).format('HH:mm:ss'));

    const newEvent = {
      title: postDetails.title,
      start: start,
      end: end,
      allDay: false,
      resource: postDetails,
    };
    setEvents([...events, newEvent]);
    closeModal();
  };

  return (
    <div className="post-scheduler">
      {/* ... Calendar component */}
      
      <Modal 
        isOpen={modalIsOpen} 
        onRequestClose={closeModal}
        className="Modal"
        overlayClassName="Overlay"
      >
        <form onSubmit={handleSubmit} className="modal-form">
          <h2>Schedule a Post</h2>
          <input 
            type="text" 
            name="title" 
            value={postDetails.title}
            onChange={handleInputChange}
            placeholder="Title" 
          />
          <textarea 
            name="description" 
            value={postDetails.description}
            onChange={handleInputChange}
            placeholder="Description"
          />
          <div className="date-time-picker">
            <DatePicker 
              selected={selectedDate} 
              onChange={handleDateChange} 
              dateFormat="MMMM d, yyyy"
              className="date-picker"
            />
            <DatePicker 
              selected={startTime} 
              onChange={date => setStartTime(date)} 
              showTimeSelect 
              showTimeSelectOnly 
              timeIntervals={15} 
              timeCaption="Start Time" 
              dateFormat="h:mm aa" 
              className="time-picker"
            />
            <DatePicker 
              selected={endTime} 
              onChange={date => setEndTime(date)} 
              showTimeSelect 
              showTimeSelectOnly 
              timeIntervals={15} 
              timeCaption="End Time" 
              dateFormat="h:mm aa" 
              className="time-picker"
            />
          </div>
          <div className="modal-actions">
            <button type="submit" className="submit-btn">Schedule</button>
            <button onClick={closeModal} className="cancel-btn">Cancel</button>
          </div>
        </form>
      </Modal>
    </div>
  );
};

export default PostScheduler;
