import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Dashboard = () => {
  const [analyticsData, setAnalyticsData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('/api/instagram/analytics');
        setAnalyticsData(response.data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching analytics data:', error);
        setError('Failed to fetch analytics data.');
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  if (loading) {
    return <div>Loading analytics...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  if (!analyticsData || analyticsData.length === 0) {
    return (
      <div>
        <h1>Instagram Analytics Dashboard</h1>
        <p>No Instagram data available. Please link your Instagram account.</p>
      </div>
    );
  }

  return (
    <div className="dashboard">
      <h1>Instagram Analytics Dashboard</h1>
      {analyticsData.map((mediaAnalytics, index) => (
        <div key={index} className="analytics-item">
          <h2>Media {index + 1} Analytics</h2>
          {/* You can adjust the details shown based on the data structure */}
          <p>Engagement: {mediaAnalytics.engagement}</p>
          <p>Impressions: {mediaAnalytics.impressions}</p>
          <p>Reach: {mediaAnalytics.reach}</p>
        </div>
      ))}
    </div>
  );
};

export default Dashboard;
