import React, { useState } from 'react';
import axios from 'axios';

function Register() {
  // State to keep track of form data
  const [formData, setFormData] = useState({
    username: '',
    password: ''
  });

  // State for error messages
  const [error, setError] = useState('');

  // Update state when form inputs change
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:3000/api/auth/register', formData);
      
      // Handle successful registration (maybe redirect or show a success message)
      console.log("Registration successful", response.data);

    } catch (err) {
      // Update state to show error message
      setError(err.response.data);
      console.error("Error in registration", err.response);
    }
  };

  return (
    <div>
      <h2>Register</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="username"
          placeholder="Username"
          value={formData.username}
          onChange={handleChange}
          required
        />
        <input
          type="password"
          name="password"
          placeholder="Password"
          value={formData.password}
          onChange={handleChange}
          required
        />
        <button type="submit">Register</button>
      </form>
      {error && <p>{error}</p>}
    </div>
  );
}

export default Register;
