import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { LineChart, Line, CartesianGrid, XAxis, YAxis, Tooltip, ResponsiveContainer } from 'recharts';

const Dashboard = () => {

const [isLinked, setIsLinked] = useState(false);
const [mediaData, setMediaData] = useState([]);
const [profileData, setProfileData] = useState(null);
const [loading, setLoading] = useState(true);
const [error, setError] = useState('');
const navigate = useNavigate()
const [plannedPosts, setPlannedPosts] = useState([]);
const [isTwitterLinked, setIsTwitterLinked] = useState(false);
  
  useEffect(() => {
    checkInstagramLink();
    checkTwitterLink();
    fetchBasicData();
    fetchPlannedPosts();
  }, []);

  const checkTwitterLink = async () => {
    try {
      // Replace with your API endpoint to check Twitter link status
      const response = await axios.get('/api/check-twitter-link');
      setIsTwitterLinked(response.data.isLinked);
    } catch (error) {
      console.error('Error checking Twitter link:', error);
      // Handle error
    }
  };

  const handleLinkTwitter = () => {
    // Redirect to the route that initiates Twitter OAuth
    window.location.href = '/api/link-twitter';
  };

  const fetchPlannedPosts = async () => {
    try {
      const response = await axios.get('/api/auth/posts'); 
      setPlannedPosts(response.data);
    } catch (error) {
      console.error('Error fetching planned posts:', error);
      
    }
  };

  const checkInstagramLink = async () => {
    try {
      const response = await axios.get('/api/instagram/check-link');
      setIsLinked(response.data.isLinked);
      if (response.data.isLinked) {
        fetchBasicData();
      }
    } catch (error) {
      console.error('Error checking Instagram link:', error);
      setError('Error checking Instagram link.');
    } finally {
      setLoading(false);
    }
  };

  
  const fetchBasicData = async () => {
    try {
      const response = await axios.get('/api/instagram/basic-data');
      setProfileData(response.data.profile);
      setMediaData(response.data.media);
    } catch (error) {
      console.error('Error fetching basic Instagram data:', error);
    }
  };

  const handleLinkInstagram = () => {
    window.location.href = '/api/instagram/oauth';
  };
  
  const navigateToScheduler = () => {
    navigate('/post-scheduler'); 
  };

  const sampleChartData = [
    { name: 'Jan', uv: 400, pv: 2400, amt: 2400 },
    { name: 'Feb', uv: 300, pv: 1398, amt: 2210 },
    // ... more data
  ];

  if (loading) { 
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  if (!isLinked) {
    return (
      <div className="dashboard">
        <h1>Instagram Analytics Dashboard</h1>
        <button onClick={handleLinkInstagram}>Link Instagram Account</button>
      </div>
    );
  }

  return (
    <div className="dashboard">
      <header className="dashboard-header">
        <h1>Instagram Analytics Dashboard</h1>
        <nav>
          <button onClick={navigateToScheduler}>
            Go to Post Scheduler
          </button>
          {isLinked ? <button onClick={handleLinkInstagram}>Re-link Instagram</button> : <button onClick={handleLinkInstagram}>Link Instagram</button>}
          <button onClick={handleLinkTwitter}>
        {isTwitterLinked ? 'Re-link Twitter' : 'Link Twitter'}
      </button>
        </nav>
      </header>
      {loading ? <div className="loading">Loading...</div> : error ? <div className="error">Error: {error}</div> : (
        <>
          {profileData && (
            <div className="profile-info">
              <h2>Profile Information</h2>
              <p>Username: {profileData.username}</p>
              <p>Account Type: {profileData.account_type}</p>
              <p>Media Count: {profileData.media_count}</p>
            </div>
          )}
          <div className="chart-container">
            <h2>Engagement Over Time</h2>
            <ResponsiveContainer width="100%" height={300}>
              <LineChart data={sampleChartData}>
                <Line type="monotone" dataKey="uv" stroke="#8884d8" />
                <CartesianGrid stroke="#ccc" />
                <XAxis dataKey="name" />
                <YAxis />
                <Tooltip />
              </LineChart>
            </ResponsiveContainer>
          </div>
          {mediaData.length > 0 && (
            <div>
              <h2>Recent Media:</h2>
              <div className="media-grid">
                {mediaData.map((media, index) => (
                  <div key={index} className="media-item">
                    <img src={media.media_url} alt={media.caption} />
                    <p>{media.caption}</p>
                  </div>
                ))}
              </div>
            </div>
          )}
        </>
      )}

<div className="section">
        <h2>Planned Posts</h2>
        {plannedPosts.length > 0 ? (
          <div className="media-grid"> {/* Use the same class as Instagram posts for consistency */}
            {plannedPosts.map((post, index) => (
              <div key={index} className="media-item">
                <h3>{post.title}</h3>
                <p>{post.description}</p>
                {post.image && (
                  <img src={post.image} alt={post.title} />
                )}
                <p className="planned-date">Planned for: {new Date(post.scheduledTime).toLocaleDateString()}</p> {/* Display the planned date */}
              </div>
            ))}
          </div>
        ) : (
          <p>No planned posts available.</p>
        )}
      </div> 
    </div>
  );
};

export default Dashboard;
