import React, { useState, useEffect } from 'react';
import axios from 'axios';
import styles from './Dashboard.css';

function Dashboard() {
    const [userData, setUserData] = useState(null);
    const [isLoading, setIsLoading] = useState(true);
    const [error, setError] = useState(null);
  
    // Function to fetch user data from the backend
    const fetchUserData = async () => {
      try {
        setIsLoading(true);
        const response = await axios.get('/api/user/data'); // Update with your actual API endpoint
        setUserData(response.data);
      } catch (error) {
        setError(error.message);
      } finally {
        setIsLoading(false);
      }
    };
  
    // useEffect to fetch data on component mount
    useEffect(() => {
      fetchUserData();
    }, []);
  
    if (isLoading) {
      return <div>Loading...</div>;
    }
  
    if (error) {
      return <div>Error: {error}</div>;
    }

  return (
    <div className={styles.dashboard}>
      <h1>Welcome to Your Dashboard</h1>
      {isLoading && <div className={styles.loading}>Loading...</div>}
      {error && <div className={styles.error}>Error: {error}</div>}
      {userData && (
        <div className={styles.analytics}>
          <h2>Instagram Analytics</h2>
          {/* Display your Instagram analytics data here */}
        </div>
      )}
      {/* Add additional components or data visualizations as needed */}
    </div>
  );
}

export default Dashboard;
