import React, { useState } from 'react';
import Calendar from 'react-calendar';
import Modal from 'react-modal';
import DatePicker from 'react-datepicker';
import './PostScheduler.css';
import 'react-calendar/dist/Calendar.css';
import 'react-datepicker/dist/react-datepicker.css';

const PostScheduler = () => {
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [postDetails, setPostDetails] = useState({
    title: '',
    description: '',
    image: null
  });

  const openModal = () => setModalIsOpen(true);
  const closeModal = () => setModalIsOpen(false);
  const handleDateChange = (date) => setSelectedDate(date);
  const handleFormChange = (e) => {
    setPostDetails({ ...postDetails, [e.target.name]: e.target.value });
  };
  const handleImageChange = (e) => {
    setPostDetails({ ...postDetails, image: e.target.files[0] });
  };
  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(postDetails, selectedDate); // For now, just log the data
    closeModal();
  };

  return (
    <div className="post-scheduler">
      <Calendar />
      <button className="add-post-btn" onClick={openModal}>Add Post</button>
      <Modal isOpen={modalIsOpen} onRequestClose={closeModal} className="modal">
        <form onSubmit={handleSubmit} className="post-form">
          <h2>Schedule a Post</h2>
          <input type="text" placeholder="Title" name="title" onChange={handleFormChange} />
          <textarea placeholder="Description" name="description" onChange={handleFormChange}></textarea>
          <input type="file" name="image" onChange={handleImageChange} />
          <DatePicker selected={selectedDate} onChange={handleDateChange} />
          <button type="submit" className="submit-btn">Schedule Post</button>
        </form>
      </Modal>
    </div>
  );
};

export default PostScheduler;
