import React, { useState, useEffect } from 'react';
import { Calendar, momentLocalizer } from 'react-big-calendar';
import moment from 'moment';
import axios from 'axios';
import Modal from 'react-modal';
import DatePicker from 'react-datepicker';
import TimePicker from 'react-time-picker';
import 'react-big-calendar/lib/css/react-big-calendar.css';
import './PostScheduler.css';
import 'react-datepicker/dist/react-datepicker.css';

const localizer = momentLocalizer(moment);

Modal.setAppElement('#root');

const PostScheduler = () => {
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [selectedTime, setSelectedTime] = useState('10:00');
  const [events, setEvents] = useState([]);
  const [postDetails, setPostDetails] = useState({ id: '', title: '', description: '', image: null });
  const [isEditMode, setIsEditMode] = useState(false);

  const openModal = () => {
    setModalIsOpen(true);
    setIsEditMode(false);
    setPostDetails({ id: '', title: '', description: '', image: null });
  };

  const openEditModal = (event) => {
    setModalIsOpen(true);
    setIsEditMode(true);
    setPostDetails({ 
      id: event.id, 
      title: event.title, 
      description: event.description, 
      image: event.image 
    });
    setSelectedDate(new Date(event.start));
    setSelectedTime(moment(event.start).format('HH:mm'));
  };

  const closeModal = () => setModalIsOpen(false);

  const handleDateChange = date => setSelectedDate(date);
  const handleTimeChange = time => setSelectedTime(time);
  const handleInputChange = e => setPostDetails({ ...postDetails, [e.target.name]: e.target.value });
  const handleImageChange = e => setPostDetails({ ...postDetails, image: e.target.files[0] });

  const handleSubmit = async (e) => {
    e.preventDefault();
    const eventDate = moment(selectedDate).set({
      hour: moment(selectedTime, 'HH:mm').hour(),
      minute: moment(selectedTime, 'HH:mm').minute(),
    }).toDate();

    const formData = new FormData();
    formData.append('title', postDetails.title);
    formData.append('description', postDetails.description);
    formData.append('date', eventDate);
    if (postDetails.image) formData.append('image', postDetails.image);

    try {
      let response;
      if (isEditMode) {
        response = await axios.put(`/api/posts/${postDetails.id}`, formData);
      } else {
        response = await axios.post('/api/posts', formData);
      }
      const updatedEvent = { ...response.data, start: new Date(response.data.date), end: new Date(response.data.date) };
      setEvents(events.map(event => event.id === updatedEvent.id ? updatedEvent : event));
      closeModal();
    } catch (error) {
      console.error('Error saving post:', error);
    }
  };

  const handleEventSelect = ({ event }) => {
    openEditModal(event);
  };

  const handleDelete = async () => {
    try {
      await axios.delete(`/api/posts/${postDetails.id}`);
      setEvents(events.filter(event => event.id !== postDetails.id));
      closeModal();
    } catch (error) {
      console.error('Error deleting post:', error);
    }
  };

  useEffect(() => {
    const fetchScheduledPosts = async () => {
      try {
        const response = await axios.get('/api/posts');
        const fetchedEvents = response.data.map(post => ({
          ...post,
          start: new Date(post.date),
          end: new Date(post.date),
        }));
        setEvents(fetchedEvents);
      } catch (error) {
        console.error('Error fetching scheduled posts:', error);
      }
    };
    fetchScheduledPosts();
  }, []);

  return (
    <div className="post-scheduler">
      <Calendar
        localizer={localizer}
        events={events}
        startAccessor="start"
        endAccessor="end"
        style={{ height: 500, margin: '50px' }}
        views={['month', 'week', 'day']}
        onSelectEvent={handleEventSelect}
      />
      <button className="add-post-btn" onClick={openModal}>Add Post</button>
      
      <Modal isOpen={modalIsOpen} onRequestClose={closeModal} className="modal">
        <form onSubmit={handleSubmit} className="modal-form">
          <h2>{isEditMode ? 'Edit Post' : 'Schedule a Post'}</h2>
          <input type="text" name="title" value={postDetails.title} onChange={handleInputChange} placeholder="Title" />
          <textarea name="description" value={postDetails.description} onChange={handleInputChange} placeholder="Description" />
          <DatePicker selected={selectedDate} onChange={handleDateChange} />
          <TimePicker onChange={handleTimeChange} value={selectedTime} />
          <input type="file" name="image" onChange={handleImageChange} />
          <button type="submit" className="submit-btn">{isEditMode ? 'Update' : 'Schedule'}</button>
          {isEditMode && <button type="button" onClick={handleDelete} className="delete-btn">Delete</button>}
          <button onClick={closeModal} className="cancel-btn">Cancel</button>
        </form>
      </Modal>
    </div>
  );
};

export default PostScheduler;
